sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    console.log("== Update Selected Records");
    console.log(JSON.stringify(event, null, 4));
    console.log(" Selected Records - stringify");
    console.log(JSON.stringify(event.payload.selectedRecords, null, 4));
    console.log(" Selected Records - object");
    console.log(event.payload.selectedRecords);
    console.log("Set/Get Value of Selected Records");
    api.setState("selectedRecords", event.payload.selectedRecords);
    console.log("== Update Selected Records");
}
